package com.example.hambergerpt;

import android.content.Intent;
import android.databinding.DataBindingUtil;
import android.support.annotation.Nullable;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.view.View;
import android.widget.Toast;

import com.example.hambergerpt.adapters.HambergerAdapter;
import com.example.hambergerpt.databinding.ActivityMainBinding;
import com.example.hambergerpt.datas.HambMenu;
import com.example.hambergerpt.datas.HambergerStores;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    ActivityMainBinding act;
    List<HambergerStores> hambergerStores = new ArrayList<>();
    HambergerAdapter hambergerAdapter;
    HambMenu hambMenuData;
    static final int INTENT_RESULT_CODE = 1;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        act= DataBindingUtil.setContentView(this, R.layout.activity_main);

        setupEvents();
        fillHambergerStores();
        setValues();

    }

    void setupEvents(){


        act.enterBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                int indexNum = act.hambergerSpinner.getSelectedItemPosition();
                HambergerStores hambergerStoresData = hambergerStores.get(indexNum);
                Intent intent = new Intent(MainActivity.this, StoreDetail.class);
                intent.putExtra("가게정보", hambergerStoresData);
                startActivityForResult(intent, INTENT_RESULT_CODE);
            }
        });

    }

    void setValues(){
        hambergerAdapter = new HambergerAdapter(MainActivity.this, hambergerStores);
        act.hambergerSpinner.setAdapter(hambergerAdapter);

    }

    void fillHambergerStores(){
        hambergerStores.add(new HambergerStores("맥도날드","강서구","10:00 ~ 20:00","010-2230-4402","data:image/jpeg;base64,"));
        hambergerStores.add(new HambergerStores("KFC","중구","11:00 ~ 23:00","010-3434-4532","data:image/png;base64,"));
        hambergerStores.add(new HambergerStores("롯데리아","종로구","9:00 ~ 22:00","010-2550-2302","data:image/png;base64,"));
        hambergerStores.add(new HambergerStores("맘스터치","강남구","8:30 ~ 19:00","010-7640-4623","data:image/jpeg;base64,"));
        hambergerStores.add(new HambergerStores("버거킹","강북구","12:00 ~ 18:00","010-8440-5342","data:image/png;base64,"));
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        if (requestCode == INTENT_RESULT_CODE){
            if (resultCode == RESULT_OK){
                hambMenuData = (HambMenu) data.getSerializableExtra("메뉴정보");
                Toast.makeText(MainActivity.this, String.format("%s 선택하여 (%d원) 지불하였습니다.",hambMenuData.menuName, hambMenuData.menuPrice), Toast.LENGTH_SHORT).show();
            }else {
                Toast.makeText(MainActivity.this, "결제를 취소하였습니다.", Toast.LENGTH_SHORT).show();
            }
        }
    }
}
